mapper.select:
select l.*,u.name as userName from log_operations l
left join users u on u.id = l.user_id


        and userId = #{request.userId}


        and content like concat('%',#{request.content},'%')


        and l.created_time >= #{request.createdTimeStart}


        and l.created_time <= #{request.createdTimeEnd}


order by l.created_time desc
