mapper.select:
select pm.*,mir.material_name as materialName from produce_material pm
         left join material_inbound_record mir on pm.material_inbound_record_id = mir.id
where pm.deleted_time is null

    and mir.material_name like concat('%',#{materialName},'%')


    and pm.created_time >= #{createdTime}


    and pm.created_time < #{createdTime1}
